            userFoods.$save(function () {
                $scope.success = true;

                $timeout(function(){$scope.success = false;}, 3000);

                if(isDelete){
                    $location.path('foods');
                }
            }, function (errorResponse) {
                $scope.error = errorResponse.data.message;
            });
