                if(radioBtnValue == 'myFoods'){
                    $scope.foods = filterMyFoods($scope.findFoodsByFirstLetter, $scope.userFoods, $scope.selected.foodSearchTxt, $scope.skipFoods);
                }
                else{
                    $scope.updateFoodList();
                }
